import ("fmt"; "math/rand"; "time")

func main(){
  var s, target, x int
  fmt.Print("inserisci target: ")
  fmt.Scan(&target)
  rand.Seed(time.Now().Unix())
  for s<=target{
    x=(rand.Intn(10))+1
    if x + s>=target{
      break
    }else{
      fmt.Print(x,"\n")
      s = s+x
    }
  }
  fmt.Println()
  fmt.Println(s)
}

