  sliceOfByte := []byte(s)
  if len(sliceOfByte) == 0 || c != sliceOfByte[0] && len(sliceOfByte) == 1 {
    return -1
  } else if c == sliceOfByte[len(sliceOfByte)-1] && ricorsiva(c, string(sliceOfByte[:len(sliceOfByte)-1])) == -1 {
    return len(sliceOfByte) - 1
  }
  return ricorsiva(c, string(sliceOfByte[:len(sliceOfByte)-1]))
}

